function Rembrandt(e){if(e!=undefined&&e[0]==="#"){this.element=document.getElementById(e.replace("#",""))}else if(e!=undefined&&e[0]=="."){this.element=document.getElementByClass(e.replace(".",""))}else if(e==undefined){this.element=document.body}}Rembrandt.prototype.fill=function(e){this.element.style.backgroundColor=e};Rembrandt.prototype.stroke=function(e,t){this.element.style.borderColor=e;this.element.style.borderWidth=t};Rembrandt.prototype.size=function(e,t){this.element.style.width=e;this.element.style.height=t};Rembrandt.prototype.opacity=function(e){if(e==="transparent"){this.element.style.opacity=0}else if(e==="translucent"){this.element.style.opacity=.5}else if(e==="opaque"){this.element.style.opacity=.8}else{this.element.style.opacity=e}};Rembrandt.prototype.move=function(e,t){this.element.style.position="absolute";this.element.style.left=e;this.element.style.top=e};Rembrandt.prototype.shape=function(e){if(e==="circle"){this.element.style.height=this.element.style.width;this.element.style.borderRadius="50%"}else if(e==="ellipse"){this.element.style.borderRadius="50%"}else if(e==="square"){this.element.style.height=this.element.style.width;this.element.style.borderRadius=0}};Rembrandt.prototype.graph=function(e,t,n,r){this.element.style.width=t;this.element.style.height=Math.max.apply(Math,e)*n;for(var i=0;i<e.length;i++){var s=document.createElement("div");s.id=this.element.id+"Bar"+String(i);s.style.backgroundColor=this.pastelColor();s.style.width=t/e.length;s.style.height=e[i]*n;this.element.style.whiteSpace="nowrap";s.style.display="inline-block";if(r!==undefined){s.style.display+="table-cell";s.style.verticalAlign="bottom";s.style.textAlign="center";s.innerText=e[i]}this.element.appendChild(s)}};Rembrandt.prototype.pastelColor=function(){var e=(Math.round(Math.random()*127)+127).toString(16);var t=(Math.round(Math.random()*127)+127).toString(16);var n=(Math.round(Math.random()*127)+127).toString(16);return"#"+e+t+n}
